    public void updateWinningResult(Context context, String site, String queryTable, 
            String[] projection, int maxStore, String latestDrawId, int parseType) {
        
        InputStream contentInput = null;
        try {
            URL url = new URI(site).toURL();
            URLConnection conn = url.openConnection();
            conn.connect();
            contentInput = conn.getInputStream();
        } catch (Exception e) {
            LottoLog.log("Request failed: " + site);
            e.printStackTrace();
            return;
        }
        InputSource inputSource = new InputSource(contentInput);
        inputSource.setEncoding("UTF-8");
        LottoLog.log(inputSource.toString());
        Parser parser = new Parser();
        try {
            parser.setProperty(Parser.schemaProperty, HtmlParser.schema);
        } catch (org.xml.sax.SAXNotRecognizedException e) {
            // Should not happen.
            throw new RuntimeException(e);
        } catch (org.xml.sax.SAXNotSupportedException e) {
            // Should not happen.
            throw new RuntimeException(e);
        }

        LottoHtmlConverterV2 converter = new LottoHtmlConverterV2(mContext, 
                inputSource, parser, latestDrawId, parseType);
        Vector<WinningInfoV2> winningInfo = new Vector<WinningInfoV2>();
        winningInfo = converter.convert();
        
        // we get the new info from the website,
        // but in database there might have the info store under the same ID
        // so we must delete the old info in database first and store the new one.
        if (!winningInfo.isEmpty()) {
            Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/" + queryTable);
            Cursor c = mContext.getContentResolver().query(table, projection, null, null, 
                    WinningInfoProvider._ID + " ASC");
            
            // 2012-12-19, Yu-An Chen
            // because now we get two issue of invoice info at the same time
            // we have to delete two old records in database if we needed
            if (queryTable.equals("Invoice")) {
                if (c != null) {
                    for (int i = 0; i < winningInfo.size(); i++) {
                        int idIndex = c
                                .getColumnIndexOrThrow(WinningInfoProvider._ID);
                        if (c.getCount() > 0) {
                            
                            int removeRecordCount = 0;// new added
                            
                            c.moveToLast();
                            do {
                                // we retrieve two issue of invoice information
                                // at the same time
                                // so we have to remove two records meanwhile if
                                // the IDs are matched
                                if (removeRecordCount == 2)
                                {
                                    break;
                                }
                                String id = c.getString(idIndex);
                                // contains old invoice data, remove them
                                if (winningInfo.get(i).drawID.contains(id)) {
                                    String[] deleteString = { c
                                            .getString(idIndex) };
                                    mContext.getContentResolver().delete(table,
                                            WinningInfoProvider._ID + "=?",
                                            deleteString);
                                    removeRecordCount++;                                    
                                }
                                if (winningInfo.get(i).drawID2.contains(id)) {
                                    String[] deleteString = { c
                                            .getString(idIndex) };
                                    mContext.getContentResolver().delete(table,
                                            WinningInfoProvider._ID + "=?",
                                            deleteString);
                                    removeRecordCount++;                                   
                                }
                            } while (c.moveToPrevious());
                            c.moveToFirst();
                        }
                        insertInvoiceWinningInfo(winningInfo.get(i));
                    }
                }
            }
            
//            if (queryTable.equals("Invoice")) {
//                if (c != null) {
//                    for (int i = 0; i < winningInfo.size(); i++) {
//                        int idIndex = c.getColumnIndexOrThrow(WinningInfoProvider._ID);
//                        if (c.getCount() > 0) {
//                            c.moveToLast();
//                            do {
//                                String id = c.getString(idIndex);
//                                if (winningInfo.get(i).drawID.contains(id)) {
//                                    String[] deleteString = {c.getString(idIndex)};
//                                    mContext.getContentResolver().delete(table, WinningInfoProvider._ID + "=?", deleteString);
//                                    break;
//                                }
//                            } while (c.moveToPrevious());
//                            c.moveToFirst();
//                        }
//                        insertInvoiceWinningInfo(winningInfo.get(i));
//                    }
//                }
//            }
            
            else {
                if (c != null) {
                    for (int i = 0; i < winningInfo.size(); i++) {
                        Boolean isExit = false;
                        int idIndex = c.getColumnIndexOrThrow(WinningInfoProvider._ID);
                        if (c.getCount() > 0) {
                            c.moveToLast();
                            do {
                                String id = c.getString(idIndex);
                                if (winningInfo.get(i).drawID.contains(id)) {
                                    isExit = true;
                                    break;
                                }
                            } while (c.moveToPrevious());
                            if (isExit == true)
                                break;
                            c.moveToFirst();
                            
                            if (c.getCount() >= maxStore) {
                                String[] deleteString = {c.getString(idIndex)};
                                mContext.getContentResolver().delete(table, WinningInfoProvider._ID + "=?", deleteString);
                            }
                        }
                        //if (queryTable.equals("Invoice")) {
                        //    insertInvoiceWinningInfo(winningInfo.get(i));
                        //} else {
                            insertLottoWinningInfo(queryTable, winningInfo.get(i));
                        //}
                    }
                }
            }
            if (c != null)
                c.close();
        }
    }
    
    private void insertLottoWinningInfo(String queryTable, WinningInfoV2 winningInfo) {
        Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/" + queryTable);
        ContentValues values = new ContentValues();
        values.put(WinningInfoProvider._ID, winningInfo.drawID);
        values.put(WinningInfoProvider.DRAW_DATE, winningInfo.drawDate);
        values.put(WinningInfoProvider.CASH_DATE, winningInfo.cashDate);
        for (int i = 0; i < WinningInfoProvider.MAX_LOTTO_WINNO; i++) {
            values.put(
                WinningInfoProvider.LOTTO_QUERY_COLUMNS[WinningInfoProvider.LOTTO_WINNO_START_POS + i], 
                winningInfo.sec1WinningNo[i]);
        }
        values.put(WinningInfoProvider.WINSEC2, winningInfo.sec2WinningNo[0]);
        for (int i = 0; i < WinningInfoProvider.MAX_LOTTO_PRIZE; i++) {
            values.put(
                WinningInfoProvider.LOTTO_QUERY_COLUMNS[WinningInfoProvider.LOTTO_PRIZE_START_POS + i], 
                winningInfo.prize[i]);
        }
        mContext.getContentResolver().insert(table, values);
    }
    
    public void getLottoWinningInfoFromDB(int lottoType) {
        
        // get the different path of lotto table depends on lottoType
        Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/"
                + WinningInfoProvider.getLottoTable(lottoType));
        Cursor c = mContext.getContentResolver().query(table,
                WinningInfoProvider.LOTTO_QUERY_COLUMNS, null, null, 
                WinningInfoProvider._ID + " ASC");
        
        mVector.removeAllElements();
        
        LottoLog.log("Lotto c.getCount()=" + c.getCount());
        if (c.getCount() == 0) {
            c.close();
            return;
        }
        
        int idIndex = c.getColumnIndexOrThrow(WinningInfoProvider._ID);
        int dateIndex = c.getColumnIndexOrThrow(WinningInfoProvider.DRAW_DATE);
        int cashIndex = c.getColumnIndexOrThrow(WinningInfoProvider.CASH_DATE);
        int winSec2Index = c.getColumnIndexOrThrow(WinningInfoProvider.WINSEC2);
        
        c.moveToLast();
        do {
            WinningInfoV2 winningInfo = new WinningInfoV2();
            winningInfo.drawID = c.getString(idIndex);
            winningInfo.drawDate = c.getString(dateIndex);
            winningInfo.cashDate = c.getString(cashIndex);
            
            // get the normal lotto ball number
            for (int i = 0; i < WinningInfoProvider.MAX_LOTTO_WINNO; i++) {
                winningInfo.sec1WinningNo[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.LOTTO_QUERY_COLUMNS[WinningInfoProvider.LOTTO_WINNO_START_POS
                                        + i]));
            }
            
            // get the special lotto ball number
            winningInfo.sec2WinningNo[0] = c.getString(winSec2Index);
            
            // get the lotto prizes
            for (int i = 0; i < WinningInfoProvider.MAX_LOTTO_PRIZE; i++) {
                winningInfo.prize[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.LOTTO_QUERY_COLUMNS[WinningInfoProvider.LOTTO_PRIZE_START_POS
                                        + i]));
            }
            mVector.add(winningInfo);
        } while (c.moveToPrevious());
        c.close();
    }
    
    public boolean getSearchLottoWinningInfoFromDB(int lottoType, String drawID) {
        
        getLottoWinningInfoFromDB(lottoType);
        
        for (int i = 0; i < mVector.size(); i++) {
            
            if (mVector.get(i).drawID.equals(drawID)) {
                WinningInfoV2 winningInfo = new WinningInfoV2();
                winningInfo = mVector.get(i);
                mVector.removeAllElements();
                mVector.add(winningInfo);
                return true;
            }
        }
        return false;
    }
    
    public String getLatestLotteryDrawIDFromDB(int lottoType) {
    	
        getLottoWinningInfoFromDB(lottoType);

        if (mVector.size() > 0)
            return mVector.get(0).drawID;
        else
            return null;
    }
    
    private String getLatestLotteryDrawDateFromDB(int lottoType) {
        getLottoWinningInfoFromDB(lottoType);

        if (mVector.size() > 0)
            return mVector.get(0).drawDate;
        else
            return null;
    }

