    public void insertInvoiceWinningInfo(WinningInfoV2 winningInfo) {
        Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/Invoice");
        
        // store the current issue of invoice winning information into database
        ContentValues values = new ContentValues();
        values.put(WinningInfoProvider._ID, winningInfo.drawID);
        for (int i =0; i < WinningInfoProvider.MAX_INVOICE_WINNO; i++) {
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_GPNO_START_POS + i], 
                winningInfo.grandPrizeNo[i]);
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_TPNO_START_POS + i], 
                winningInfo.topPrizeNo[i]);
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_FPNO_START_POS + i], 
                winningInfo.firstPrizeNo[i]);
        }
        for (int i =0; i < WinningInfoProvider.MAX_INVOICE_SIXTH_WINNO; i++) {
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_SPNO_START_POS + i], 
                winningInfo.sixthPrizeNo[i]);
        }
        values.put(WinningInfoProvider.GRANDPRIZE, winningInfo.grandPrize);
        values.put(WinningInfoProvider.TOPPRIZE, winningInfo.topPrize);
        values.put(WinningInfoProvider.FIRSTPRIZE, winningInfo.firstPrize);
        values.put(WinningInfoProvider.SIXTHPRIZE, winningInfo.sixthPrize);
        mContext.getContentResolver().insert(table, values);
        
        // store the previous issue of invoice winning information into database
        values = new ContentValues();
        values.put(WinningInfoProvider._ID, winningInfo.drawID2);
        for (int i =0; i < WinningInfoProvider.MAX_INVOICE_WINNO; i++) {
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_GPNO_START_POS + i], 
                winningInfo.grandPrizeNo2[i]);
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_TPNO_START_POS + i], 
                winningInfo.topPrizeNo2[i]);
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_FPNO_START_POS + i], 
                winningInfo.firstPrizeNo2[i]);
        }
        for (int i =0; i < WinningInfoProvider.MAX_INVOICE_SIXTH_WINNO; i++) {
            values.put(
                WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_SPNO_START_POS + i], 
                winningInfo.sixthPrizeNo2[i]);
        }
        
        values.put(WinningInfoProvider.GRANDPRIZE, winningInfo.grandPrize);
        values.put(WinningInfoProvider.TOPPRIZE, winningInfo.topPrize);
        values.put(WinningInfoProvider.FIRSTPRIZE, winningInfo.firstPrize);
        values.put(WinningInfoProvider.SIXTHPRIZE, winningInfo.sixthPrize);
        mContext.getContentResolver().insert(table, values);
    }
    
