    public void getInvoiceWinningInfoFromDB() {
        Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/Invoice");
        Cursor c = mContext.getContentResolver().query(table,
                WinningInfoProvider.INVOICE_QUERY_COLUMNS, null, null,
                WinningInfoProvider._ID + " DESC");

        mVector.removeAllElements();

        LottoLog.log("Invoice c.getCount()=" + c.getCount());
        if (c.getCount() == 0) {
            c.close();
            return;
        }

        int idIndex = c.getColumnIndexOrThrow(WinningInfoProvider._ID);

        c.moveToLast();
        do {
            WinningInfoV2 winningInfo = new WinningInfoV2();
            winningInfo.drawID = c.getString(idIndex);
            for (int i = 0; i < WinningInfoProvider.MAX_INVOICE_WINNO; i++) {
                winningInfo.grandPrizeNo[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_GPNO_START_POS
                                        + i]));
                winningInfo.topPrizeNo[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_TPNO_START_POS
                                        + i]));
                winningInfo.firstPrizeNo[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_FPNO_START_POS
                                        + i]));
            }
            for (int i = 0; i < WinningInfoProvider.MAX_INVOICE_SIXTH_WINNO; i++) {
                winningInfo.sixthPrizeNo[i] = c
                        .getString(c
                                .getColumnIndexOrThrow(WinningInfoProvider.INVOICE_QUERY_COLUMNS[WinningInfoProvider.INVOICE_SPNO_START_POS
                                        + i]));
            }
            winningInfo.grandPrize = c.getString(c
                    .getColumnIndexOrThrow(WinningInfoProvider.GRANDPRIZE));
            winningInfo.topPrize = c.getString(c
                    .getColumnIndexOrThrow(WinningInfoProvider.TOPPRIZE));
            winningInfo.firstPrize = c.getString(c
                    .getColumnIndexOrThrow(WinningInfoProvider.FIRSTPRIZE));
            winningInfo.sixthPrize = c.getString(c
                    .getColumnIndexOrThrow(WinningInfoProvider.SIXTHPRIZE));
            mVector.add(winningInfo);
        } while (c.moveToPrevious());

        c.close();
    }

    public Vector<WinningInfoV2> postHttpWinningResult(String queryTable,
            String[] projection, int maxStore, String drawno, int index, String monthPos, String yearPos) {

        if (monthPos.length() > 0)
            isSearchByDarw = false;
        else
            isSearchByDarw = true;
        HttpClient hc = new DefaultHttpClient();
        HttpPost hp = new HttpPost(getLottoSite(index));

        try {
            List<NameValuePair> params = addParamForSearch(index, drawno,
                    yearPos, monthPos);
            hp.setEntity(new UrlEncodedFormEntity(params, HTTP.UTF_8));
            HttpResponse hrs = hc.execute(hp);
            String content = EntityUtils.toString(hrs.getEntity());

            Parser parser = new Parser();
            try {
                parser.setProperty(Parser.schemaProperty, HtmlParser.schema);
            } catch (org.xml.sax.SAXNotRecognizedException e) {
                // Should not happen.
                throw new RuntimeException(e);
            } catch (org.xml.sax.SAXNotSupportedException e) {
                // Should not happen.
                throw new RuntimeException(e);
            }

           LottoHtmlConverterV2 converter = new LottoHtmlConverterV2(mContext, 
                    content, parser, null, WinningInfoV2.PARSE_LOTTO);

            Vector<WinningInfoV2> winningInfo = new Vector<WinningInfoV2>();
            winningInfo = converter.convert();

            return winningInfo;

        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }
    
    private List<NameValuePair> getSearchparam(String drawno, String listno, int postnum, String yearPos, String monthPos) {
        
        List<NameValuePair> params = new ArrayList<NameValuePair>();
        
        params.add(new BasicNameValuePair(mSearchPost[postnum][0], listno));
        params.add(new BasicNameValuePair(mSearchPost[postnum][3], mContext.getString(R.string.convert_check_search)));
        params.add(new BasicNameValuePair("__VIEWSTATE", mSearchPost[postnum][7]));
        params.add(new BasicNameValuePair("__EVENTVALIDATION", mSearchPost[postnum][8]));
     
        if (!isSearchByDarw) {
            params.add(new BasicNameValuePair(mSearchPost[postnum][4], "radYM"));
            params.add(new BasicNameValuePair(mSearchPost[postnum][5], yearPos));
            params.add(new BasicNameValuePair(mSearchPost[postnum][6], monthPos));
        } else {
            params.add(new BasicNameValuePair(mSearchPost[postnum][1], "radNO"));
            params.add(new BasicNameValuePair(mSearchPost[postnum][2], drawno));
        }
     
        if (postnum == 1 || postnum == 6 || postnum == 7 || postnum == 8)
            params.add(new BasicNameValuePair("__VIEWSTATEENCRYPTED", ""));
        
        return params;
    }

    private List<NameValuePair> addParamForSearch(int index, String drawno, String yearPos, String monthPos) {
        
        List<NameValuePair> params = null;

        switch (index) {
        case WinningInfoV2.LOTTO_WEILI:
            params = getSearchparam(drawno, POST_WEILI, 0, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_BIG: 
            params = getSearchparam(drawno, POST_BIGLOTTO, 1, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_539:
            params = getSearchparam(drawno, POST_539, 2, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_TICKTACKTOE:
            params = getSearchparam(drawno, POST_TTT, 5, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_STAR3:
            params = getSearchparam(drawno, POST_3STAR, 3, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_STAR4:
            params = getSearchparam(drawno, POST_4STAR ,4, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_38:
            params = getSearchparam(drawno, POST_38, 6, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_49:
            params = getSearchparam(drawno, POST_49, 7, yearPos, monthPos);
            break;
        case WinningInfoV2.LOTTO_39:
            params = getSearchparam(drawno, POST_39, 8, yearPos, monthPos);
            break;
        }
        return params;
    }
    
    public boolean isAutoRefreshNeeded(int lottoType) {
        // Check Auto refresh setting
        SharedPreferences prefs = mContext.getSharedPreferences("com.diordnaapps.twlotto_preferences", 0);
        Boolean bRefresh = prefs.getBoolean("twlotto_setting_refresh", true);
        if (!bRefresh)
            return false;
        
        // No info in DB, force refresh
        String latestDate = getLatestLotteryDrawDateFromDB(lottoType);
        if (latestDate == null)
            return true;
        
        Date today = new Date();
        
        Date lottodate = new Date(latestDate);
        lottodate.setYear(lottodate.getYear() + 1911);
        
        long diffInDays = ((today.getTime() - lottodate.getTime()) / 
                (1000 * 60 * 60 * 24));
        
        // Latest update is done today
        if (diffInDays == 0) {
            return false;
        }
        
        // Sunday (no draw day), and latest update is day before
        if (today.getDay() == 0 && lottodate.before(today)) {
            return false;
        }
        
        if (today.getDay() == 1 || today.getDay() == 2) diffInDays--; //skip Sunday
        
        if (diffInDays > 3) //force update if more than 3 days, max gap days between draw
            return true;
        
        switch (lottoType) {
            case WinningInfoV2.LOTTO_539:
            case WinningInfoV2.LOTTO_39:
            case WinningInfoV2.LOTTO_TICKTACKTOE:
            case WinningInfoV2.LOTTO_STAR3:
            case WinningInfoV2.LOTTO_STAR4:
                if (diffInDays > 1 || (diffInDays == 1 && today.getHours() >= WinningInfoV2.LOTTO_DRAW_HOUR))
                    return true;
                break;
            case WinningInfoV2.LOTTO_WEILI:
            case WinningInfoV2.LOTTO_38:
                //Monday and Thursday after draw time
                if ((today.getDay() == 1 || today.getDay() == 4) && today.getHours() >= WinningInfoV2.LOTTO_DRAW_HOUR)
                    return true;
                break;
            case WinningInfoV2.LOTTO_BIG:
            case WinningInfoV2.LOTTO_49:
                //Tuesday and Friday after draw time
                if ((today.getDay() == 2 || today.getDay() == 5) && today.getHours() >= WinningInfoV2.LOTTO_DRAW_HOUR)
                    return true;
                break;
        }
        return false;
    }
    
    public void deleteOldInvoiceRecords(int maxStore) {
        Uri table = Uri.parse(WinningInfoProvider.CONTENT_URI + "/" + "Invoice");
        Cursor c = mContext.getContentResolver().query(table, 
                WinningInfoProvider.INVOICE_QUERY_COLUMNS, null, null, null);
        int idIndex = c.getColumnIndexOrThrow(WinningInfoProvider._ID);
        
        if (c != null) {
            if (c.getCount() > maxStore) {
                int records = c.getCount();
                c.moveToFirst();
                for (int i = 0; i < (records - maxStore); i++) {
                    String[] deleteString = {c.getString(idIndex)};
                    mContext.getContentResolver().delete(table, WinningInfoProvider._ID + "=?", deleteString);
                    c.moveToNext();
                }
            }
        }
        if (c != null)
            c.close();
    }
